import React, { Component } from 'react'
import axios from 'axios'
import * as url from '../include/var.js'
import {
    BrowserRouter as Router,
    withRouter,
    useParams,
    Link
} from "react-router-dom"

class FirstApi extends Component {
    state = {
        productgroups: [],

        error: null,
    }



    render() {
        const { error, productgroups, listgroups } = this.state;

        // Print errors if any
        if (error) {
            return <div>An error occured: {error.message}</div>;
        }

        return (
            <div className="App product__grid">
                    <div className="card card-bordered">
                        <div className='icons__right'>
                            {/* <Link to={url.product_edit + '/' + productgroup.id + '/' + productgroup.attributes.picproductgroup.data.id} ><img src={url.icon_edit} /></Link> */}
                            <img onClick={(e) => this.deleteRow(productgroup.id, e)} src={url.icon_delete} />
                        </div>

                        <img className="card-img-top img-fluid" src={url.api_url + productgroup.attributes.picproductgroup.data.attributes.url} alt="image" />
                        <h5 className='title'>{productgroup.attributes.titleproductgroup}</h5>
                        {console.log(productgroup.attributes.picproductgroup.data.id)}
                        <Link to={'/ListProduct/' + productgroup.id} ><p className="btn btn-primary">Regarder</p></Link>
                    </div>

            </div>
        );
    }
}

export default FirstApi
